import React, { useState } from 'react';
import { TextField, Button, Checkbox, FormControlLabel, Typography, Container, Box } from '@material-ui/core';

function RegistrationForm() {
  // State for form data and errors
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    termsAccepted: false,
  });
  const [errors, setErrors] = useState({});

  // Validation functions based on the criteria provided
  const validateForm = () => {
    let formIsValid = true;
    let newErrors = {};

    // Add all the validation logic here as per the document

    setErrors(newErrors);
    return formIsValid;
  };

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, checked } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'termsAccepted' ? checked : value,
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Registration successful');
      // Handle registration logic here
    }
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" align="center" gutterBottom>Registration</Typography>
      <form onSubmit={handleSubmit}>
        <TextField
          label="First Name"
          name="firstName"
          variant="outlined"
          fullWidth
          margin="normal"
          value={formData.firstName}
          onChange={handleChange}
          error={!!errors.firstName}
          helperText={errors.firstName}
        />
        {/* Repeat for other fields */}
        <FormControlLabel
          control={<Checkbox checked={formData.termsAccepted} onChange={handleChange} name="termsAccepted" />}
          label="I accept all terms & conditions"
        />
        <Box mt={2}>
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Register Now
          </Button>
        </Box>
      </form>
      <Typography align="center">
        Already have an account? Login now
      </Typography>
    </Container>
  );
}

export default RegistrationForm;
